mapper.update:
 	update tbl_notice set
	notice_view  =  notice_view + 1
	where notice_no = #{notice_no}

mapper.select:
		select * from(
			select rownum rnum, a.* from(
        		select * from tbl_faq order by faq_no desc
        	)a)
		where rnum between #{startRow} and #{endRow}

mapper.update:
		update tbl_faq set
    		faq_view = faq_view + 1
		where faq_no = #{review_no}
